var number = flow.get("number");

// Check if there is an existing object in the array with the same Number and Topic
var indexToUpdate = questions.findIndex(function (q) {
    return q.Number === number && q.Topic === topic;
});

if (indexToUpdate === -1) {
    // If no existing object is found, add the new_question object to the end of the array
    var new_question = {
        "Number": number,
        "Topic": topic,
        "Question": question,
        "A1": A1,
        "A2": A2,
        "A3": A3,
        "A4": A4,
        "Correct": correct
    };
    questions.push(new_question);
} else {
    // If an existing object is found, update that object with the new information
    questions[indexToUpdate].Question = question;
    questions[indexToUpdate].A1 = A1;
    questions[indexToUpdate].A2 = A2;
    questions[indexToUpdate].A3 = A3;
    questions[indexToUpdate].A4 = A4;
    questions[indexToUpdate].Correct = correct;
}

flow.set("questions", questions);

